CREATE TABLE Transactions (
    transaction_id INTEGER PRIMARY KEY,
    transaction_date TEXT DEFAULT (DATETIME('now')), -- Changed type to TEXT, uses DATETIME function
    items TEXT, 
    total_amount REAL  -- Changed type to REAL for floating-point numbers
);

CREATE TABLE Articles (
    article_id INTEGER PRIMARY KEY,
    name TEXT unique,
    price REAL,
    stock REAL,
    type TEXT,
    subtype TEXT
);


