--retrieve transactions and their content that occurred today
select "transaction".transaction_id as ID, article.name as Artikel, in_transaction.amount as Pocet, "transaction".created_at as Vytoverene
from in_transaction 
join "transaction" on in_transaction.transaction_id = "transaction".transaction_id
join article on in_transaction.article_id = article.article_id where date(created_at) = date('now');

--select last id in transaction
select transaction_id from "transaction" order by transaction_id desc limit 1;

--create transaction
insert into "transaction" (transaction_id, cashier_id, created_at, customer_id)
values (?,?,current_timestamp, ?);

--transaction of the day number
select count(*) from "transaction" 
where date(created_at) = date('now');

--get articles from transaction
select name, amount, price_at_sale from in_transaction 
join article on in_transaction.article_id = article.article_id 
where transaction_id = 1;

--remove x amount from transaction
update article set stock = stock - ? where article_id = ?;

--remove x amount from transaction
update article set stock = stock + ? where article_id = ?;

--insert article into transaction
insert into in_transaction (article_id, transaction_id, amount, price_at_sale) 
values (?,?,?,?);

select subtype_name from subtype join type on subtype.parent_type_id = type.type_id where type_name = "Food";


BEGIN TRANSACTION;

-- 1. Ensure enough stock exists
SELECT
    CASE
        WHEN stock < :add_amount THEN
            RAISE(ABORT, 'Not enough stock')
    END
FROM article
WHERE article_id = :article_id;

-- 2. Insert or update article in transaction
INSERT INTO in_transaction (
    transaction_id,
    article_id,
    amount,
    price_at_sale
)
VALUES (
    :transaction_id,
    :article_id,
    :add_amount,
    :price_at_sale
)
ON CONFLICT(transaction_id, article_id)
DO UPDATE SET
    amount = amount + :add_amount;

-- 3. Decrease stock
UPDATE article
SET stock = stock - :add_amount
WHERE article_id = :article_id;

COMMIT;


UPDATE in_transaction set amount = amount - 2 where transaction_id = 268 and article_id = 1;
UPDATE article set amount = amount + ? where article_id = ?;
